import timeimport jsonimport randomimport scrapyclass pigat_whois(scrapy.Spider):	name = 'pigat_dujitang'	def start_requests(self):		headers = {'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:69.0) Gecko/20100101 Firefox/69.0'}		dujitang_url = 'https://api.vvhan.com/api/ian?type=json'		yield scrapy.Request(dujitang_url, headers=headers, callback=self.dujitang)	def dujitang(self, response):		dujitang_json = json.loads(response.text)		print('\n\033[1;34m[{}] {}\n\033[0m'.format(time.strftime('%Y-%m-%d %H:%M:%S'),			                                               dujitang_json['ishan']))